import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';
import Notes from './Components/dataList'
function App() {
  const indata={
      width:'35vw',
      padding:'10px',
      border:'2px solid black',
      height:'50vw',
      overFlow:'hidden',
      backgroundColor:'#88E7C0'
  }

  const outdata={
      margin:0,
      padding:0,

      float:'left'
  }

  const inpdata={
        widht:'350px',
        hieght:'10vw'
  }

  const btn={
    height:'28px'
  }

  const container1={
        alignItems:'center',
          justyifyContent:'center',
          padding:'35%',
          paddingTop:'15%',

  }

  //css ends 

  const [item, setItem] = useState();
  const [listd, setListd]=useState([]);


  const dataset=(e)=>{
      setItem(e.target.value);
  }

  const clickedd=()=>{
      setListd((oldItems)=>{
        return[...oldItems, item]
      })

      setItem('');
  }
  return (
    <div className="App">
    <div style={container1}>
          <div style={indata}>
                <input type='text' style={{width:'250px',height:'20px'}} name='inlist' placeholder="enter item" onChange={dataset} value={item} />
                <button style={btn} onClick={clickedd}>Add</button>
          <div style={outdata}>
            {listd.map((v, i)=>{
                          return(

                              <Notes item={v}/>
                            )

                        })}
          </div>
          </div>
    </div>  
    </div>
  );
}

export default App;
