import React, {useState,useEffect} from 'react';
import Notes from './Components/hooksdata/Notes';


const butt={
    borderRadius:'50%',
    width:'50px',
    height:'50px',
    padding:0,
    marginLeft:'15px',
}

const inputbox={
    width:'300px',
    height:'40px',
    borderRadius:'20px',
    border:'none',

}

const tpanel={
  alignItems:'center',
  display:'flex',
  justifyContent:'center',
  marginTop:'7vw',

}

const tpanel1={
  backgroundColor:'#00C6FF',
  width:'30vw',
  border:'2px solid black',
  alignItems:'center',
  padding:'2.5vw',
  height:'30vw',
}

const vpanel={
  overflow:'hidden',
  margin:0,
  padding:0,


}
function App(){
  const [itemlist, setItemlist]=useState();

  const [itemlis, setItemlis]=useState([]);



  const inptbox=(e)=>{
      setItemlist(e.target.value);

  }

  const AddItem=(e)=>{
      setItemlis((oldItems)=>{
          return [...oldItems, itemlist];
        })
      setItemlist('');
  }


  const Delete=(id)=>{
    console.log('hwllo');

    setItemlis((oldItems)=>{
            return oldItems.filter((arr, index)=>{
              return(index!==id);    
      })
    })
  }

    return(
        <>

        <div style={tpanel}>
          <div style={tpanel1}>

          <div>
              <input type='text' style={inputbox} placeholder='Items' name='additemc' onChange={inptbox} value={itemlist} />
              <button onClick={AddItem} style={butt}>+</button>
          </div>
            <div style={vpanel}>
              {itemlis.map((b,i)=>{

                  return(
                    <Notes item={b} onSelect={Delete} key={i} id={i} />
                  )
              })}  
            </div>
          </div>

        </div>

        </>

      )

  }


export default App;
